const parts = msg.topic.split('/');
const hospital = parts[1].toLowerCase();
const tipo_evento = parts[3];
const data = msg.payload;

// Los datos vienen planos (no anidados en data.paciente)
msg.measurement = "eventos_" + hospital;
msg.payload = {
    tipo_evento: tipo_evento,
    nivel_triaje_nombre: String(data.nivel_triaje || ""),
    patologia: String(data.patologia || "no_especificada"),
    paciente_id: String(data.paciente_id || ""),
    edad: Number(data.edad) || 0,
    tiempo_total: Number(data.tiempo_total) || 0,
    tiempo_espera: Number(data.tiempo_espera_atencion) || 0,
    destino: String(data.destino || ""),
    derivado_a: String(data.derivado_a || ""),
    valor: 1
};

node.status({fill:"green", shape:"dot", text: hospital + " - " + tipo_evento + " (edad:" + msg.payload.edad + ")"});
return msg;

// Extraer hospital del topic
const parts = msg.topic.split('/');
const hospital = parts[1].toLowerCase();
const data = msg.payload;

// Measurement incluye hospital para poder agrupar
msg.measurement = "stats_" + hospital;

// Todos los campos deben coincidir EXACTAMENTE con lo que env√≠a simulador.py
msg.payload = {
    // Boxes - forzar a entero
    boxes_ocupados: Math.round(Number(data.boxes_ocupados) || 0),
    boxes_totales: Math.round(Number(data.boxes_totales) || 0),
    ocupacion_boxes: Number(data.ocupacion_boxes) || 0,

    // Observaci√≥n - el simulador env√≠a "observacion_ocupadas" (con 's')
    observacion_ocupadas: Math.round(Number(data.observacion_ocupadas) || 0),
    observacion_totales: Math.round(Number(data.observacion_totales) || 0),
    ocupacion_observacion: Number(data.ocupacion_observacion) || 0,

    // Pacientes en espera
    pacientes_en_espera_triaje: Math.round(Number(data.pacientes_en_espera_triaje) || 0),
    pacientes_en_espera_atencion: Math.round(Number(data.pacientes_en_espera_atencion) || 0),

    // Tiempos
    tiempo_medio_espera: Number(data.tiempo_medio_espera) || 0,
    tiempo_medio_atencion: Number(data.tiempo_medio_atencion) || 0,
    tiempo_medio_total: Number(data.tiempo_medio_total) || 0,

    // Contadores hora - el simulador env√≠a "pacientes_atendidos_hora" y "pacientes_llegados_hora"
    pacientes_atendidos_hora: Math.round(Number(data.pacientes_atendidos_hora) || 0),
    pacientes_llegados_hora: Math.round(Number(data.pacientes_llegados_hora) || 0),
    pacientes_derivados: Math.round(Number(data.pacientes_derivados) || 0),

    // Saturaci√≥n
    nivel_saturacion: Number(data.nivel_saturacion) || 0,
    emergencia_activa: data.emergencia_activa ? 1 : 0
};

const ocupacion = Math.round((data.ocupacion_boxes || 0) * 100);
let color = "green";
if (ocupacion > 70) color = "yellow";
if (ocupacion > 85) color = "red";
node.status({fill: color, shape:"dot", text: hospital + ": " + ocupacion + "% (" + msg.payload.boxes_ocupados + "/" + msg.payload.boxes_totales + ")"});

return msg;

const data = msg.payload;

msg.measurement = "coordinador";
msg.payload = {
    emergencia_activa: data.emergencia_activa ? 1 : 0,
    tipo_emergencia: String(data.tipo_emergencia || "ninguna"),
    derivaciones_totales: Number(data.derivaciones_totales) || 0,
    minutos_ahorrados: Number(data.minutos_ahorrados) || 0,
    alertas_emitidas: Number(data.alertas_emitidas) || 0
};

if (data.emergencia_activa) {
    node.status({fill:"red", shape:"ring", text: "EMERGENCIA: " + data.tipo_emergencia});
} else {
    node.status({fill:"green", shape:"dot", text: "Derivaciones: " + msg.payload.derivaciones_totales});
}
return msg;

const data = msg.payload;

msg.measurement = "alertas";
msg.payload = {
    tipo: String(data.tipo || "desconocido"),
    nivel: String(data.nivel || "info"),
    hospital: String(data.hospital_id || "sistema"),
    mensaje: String(data.mensaje || ""),
    valor: 1
};

let color = "blue";
if (data.nivel === "warning") color = "yellow";
if (data.nivel === "critical") color = "red";
node.status({fill: color, shape:"ring", text: data.tipo + " - " + (data.hospital_id || "sistema")});
return msg;

// Solo pasar alertas cr√≠ticas
if (msg.payload.nivel === "critical") {
    const mensaje = "üö® ALERTA CR√çTICA [" + msg.payload.hospital + "]: " + msg.payload.mensaje;
    msg.payload = mensaje;
    return msg;
}
return null;

const parts = msg.topic.split('/');
const hospital = parts[1].toLowerCase();
const data = msg.payload;

// Calcular disponibles y porcentaje desde los datos del simulador
const ocupados = Number(data.ocupados) || 0;
const totales = Number(data.totales) || 0;
const disponibles = totales - ocupados;
const porcentaje = totales > 0 ? (ocupados / totales) * 100 : 0;

msg.measurement = "recursos_" + hospital;
msg.payload = {
    tipo_recurso: "boxes",
    ocupados: ocupados,
    totales: totales,
    disponibles: disponibles,
    porcentaje: porcentaje,
    en_cola: Number(data.en_cola) || 0
};

node.status({fill:"blue", shape:"dot", text: hospital + ": " + ocupados + "/" + totales});
return msg;

if (!msg.payload || msg.payload.length === 0) {
    node.status({fill:"grey", shape:"ring", text: "Sin datos"});
    return null;
}

let resumen = "";
msg.payload.forEach(row => {
    // Extraer hospital del measurement (stats_chuac -> chuac)
    const measurement = row._measurement || "";
    const hospital = measurement.replace("stats_", "").toUpperCase();
    const saturacion = Math.round((row._value || 0) * 100);
    resumen += hospital + ": " + saturacion + "% | ";
});

node.status({fill:"blue", shape:"dot", text: resumen.slice(0, -3)});

return msg;

const parts = msg.topic.split('/');
const hospital = parts[1].toLowerCase();
const data = msg.payload;

if (!data.predicciones || !Array.isArray(data.predicciones)) {
    return null;
}

// Guardar en alertas_prediccion con tipo 'prediccion'
data.predicciones.forEach(pred => {
    const newMsg = RED.util.cloneMessage(msg);
    newMsg.measurement = "alertas_prediccion";
    newMsg.payload = {
        tipo: "prediccion",
        hospital: hospital,
        llegadas_esperadas: Number(pred.llegadas_esperadas) || 0,
        minimo: Number(pred.minimo) || 0,
        maximo: Number(pred.maximo) || 0,
        hora: Number(pred.hora) || 0,
        z_score: 0
    };
    newMsg.timestamp = new Date(pred.timestamp);
    node.send(newMsg);
});

node.status({fill:"purple", shape:"dot", text: hospital + ": " + data.predicciones.length + " predicciones"});
return null;

const data = msg.payload;

// Guardar alerta en alertas_prediccion
msg.measurement = "alertas_prediccion";
msg.payload = {
    tipo: String(data.tipo || "anomalia"),
    nivel: String(data.nivel || "warning"),
    mensaje: String(data.mensaje || ""),
    hospital: String(data.hospital || "sistema"),
    z_score: Number(data.z_score) || 0,
    valor: 1
};

let color = "yellow";
if (data.nivel === "critical") color = "red";
node.status({fill: color, shape:"ring", text: data.tipo + ": " + (data.hospital || "regional")});

// Tambi√©n mostrar en debug si es cr√≠tico
if (data.nivel === "critical") {
    node.warn("üîÆ ALERTA PREDICCI√ìN: " + data.mensaje);
}

// Si hay z_score y hospital, crear entrada adicional en 'anomalias'
if (data.z_score && data.hospital) {
    const anomaliaMsg = {
        measurement: "anomalias",
        payload: {
            hospital: String(data.hospital),
            z_score: Number(data.z_score),
            detectada: 1,
            tipo: String(data.tipo || "anomalia")
        }
    };
    return [msg, anomaliaMsg];
}

return [msg, null];
